module.exports = (function (promise, $) {
    var baseUrl = "http://localhost:9009";
    return {
        post: function (url, data) {
            var Promise = promise.Promise;
            return new Promise(function (resolve, reject) {
                $.ajax({
                    url: url,
                    data: JSON.stringify(data),
                    method: "POST",
                    dataType: "json",
                    contentType: "application/json",
                    success: resolve,
                    error: reject
                });
            });
        },
        get: function (url) {
            var Promise = promise.Promise;
            return new Promise(function (resolve, reject) {
                $.ajax({
                    url: url,
                    method: "GET",
                    dataType: "json",
                    success: resolve,
                    error: reject
                });
            });
        },
        del: function (url) {
            var Promise = promise.Promise;
            return new Promise(function (resolve, reject) {
                $.ajax({
                    url: url,
                    method: "DELETE",
                    dataType: "json",
                    success: resolve,
                    error: reject
                });
            });
        }
    }
})(require("es6-promise"), require("jquery"));